import React from 'react';
import './PageStyles.css';

const PrivacyPolicy = () => {
  return (
    <div className="page-container">
      <h1>Privacy Policy</h1>
      <p>
        Your privacy is important to us. This policy outlines how we collect, use, and protect your
        personal information.
      </p>
      <h2>1. Information We Collect</h2>
      <p>
        We may collect personal information such as your name, email address, and payment details
        when you use our services.
      </p>
      <h2>2. How We Use Your Information</h2>
      <p>
        We use your information to process transactions, improve our services, and communicate with
        you.
      </p>
      <h2>3. Data Protection</h2>
      <p>
        We implement security measures to protect your personal information from unauthorized access
        or disclosure.
      </p>
    </div>
  );
};

export default PrivacyPolicy;
