import React, { useEffect, useState } from 'react';
import './ContactSection.css';

const ContactSection = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const toggleVisibility = () => {
      if (window.scrollY > 400) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", toggleVisibility);
    return () => window.removeEventListener("scroll", toggleVisibility);
  }, []);

  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  return (
    <>
      {/* ðŸ‘‡ Section pura yahan tak hai */}
      <section className="join-section">
        <h1>Want To Join Us?</h1>
        <img src="/assets/union.png" alt="Join Us" className="contact-top-image" />

        <div className="social-icons">
          <img src="/assets/link.png" alt="LinkedIn" />
          <img src="/assets/face.png" alt="Facebook" />
          <img src="/assets/insta.png" alt="Instagram" />
          <img src="/assets/youtube.png" alt="YouTube" />
          <img src="/assets/twitter.png" alt="Tumblr" />
          <img src="/assets/print.png" alt="Pinterest" />
        </div>

        <footer className="footer-wrapper">
          <div className="footer">
            <div className="footer-left">
              <h2>Krutsha</h2>
              <p>Copyright Â© 2027 by<br />Krutsha, Inc. All<br />rights reserved.</p>
            </div>
            <div className="footer-right">
              {/* Footer Columns */}
            </div>
          </div>
        </footer>
      </section>

      {/* ðŸ‘‡ Scroll Button OUTSIDE Section */}
      {isVisible && (
        <button className="scroll-to-top" onClick={scrollToTop}>
          <span className="arrow-up">â†‘</span>
        </button>
      )}
    </>
  );
};

export default ContactSection;
