// src/components/ServiceSection.js
import React from 'react';
import './ServiceSection.css';

function ServiceSection() {
  return (
    <section className="service-section">
      <div className="service-container">
        {/* Left Side */}
        <div className="service-left">
          <h2 className="service-heading">
            <span className="highlight">Academic doubts?</span> No more!
          </h2>
          <p className="service-para">
            Just ask your doubt and <span className="highlight">Krutsha</span> instantly provides the answer.
            From math to science, we’ve got you covered – it’s like having a personal teacher in your pocket.
          </p>
          <div className="image-wrapper image-main">
            <img src="/assets/service1.png" alt="Main service" />
          </div>
        </div>

        {/* Right Side */}
        <div className="service-right">
          <div className="image-wrapper image-top">
            <img src="/assets/service2.png" alt="Top service" />
          </div>
          <div className="image-wrapper image-bottom">
            <img src="/assets/service3.png" alt="Bottom service" />
          </div>
        </div>
      </div>
    </section>
  );
}

export default ServiceSection;
