// src/components/ServiceSection.js
import React from 'react';
import './ServiceSection.css';

function ServiceSection() {
  return (
    <section className="service-grid-section">
      <div className="grid-container">
        {/* Block 1 - Top Left - Text */}
        <div className="grid-item text-block">
          <h2>Instant doubt-solving for school students</h2>
          <p>
            Ask any doubt and get <span className="highlight">instant video solutions</span> for Maths, Science, Social Science and more!
          </p>
        </div>

        {/* Block 2 - Top Right - Image */}
        <div className="grid-item image-block">
          <img src="/assets/Rectangle 4.png" alt="Top Right" />
        </div>

        {/* Block 3 - Bottom Left - Image */}
        <div className="grid-item image-block">
          <img src="/assets/Rectangle 4.png" alt="Bottom Left" />
        </div>

        {/* Block 4 - Bottom Right - Image */}
        <div className="grid-item image-block">
          <img src="/assets/Rectangle 4.png" alt="Bottom Right" />
        </div>
      </div>
    </section>
  );
}

export default ServiceSection;
