import React from 'react';
import './PageStyles.css';

const RefundPolicy = () => {
  return (
    <div className="page-container">
      <h1>Refund Policy</h1>
      <p>
        We want you to be satisfied with your purchase. If you are not happy, you may be eligible for
        a refund under the following conditions.
      </p>
      <h2>1. Eligibility for Refund</h2>
      <p>
        Refund requests must be made within 14 days of purchase. Products must be unused and in
        original packaging.
      </p>
      <h2>2. Non-Refundable Items</h2>
      <p>
        Certain items such as downloadable software and personalized goods are non-refundable.
      </p>
      <h2>3. How to Request a Refund</h2>
      <p>
        Please contact our customer support team with your order details to initiate the refund
        process.
      </p>
    </div>
  );
};

export default RefundPolicy;
