import React from 'react';
import './PageStyles.css';

const TermsOfService = () => {
  return (
    <div className="page-container">
      <h1>Terms of Service</h1>
      <p>
        Welcome to our website. By accessing or using our site, you agree to comply with and be bound
        by the following terms and conditions. Please read them carefully.
      </p>
      <h2>1. Use of the Site</h2>
      <p>
        This website is provided for your personal and non-commercial use only. Any unauthorized use
        may result in legal action.
      </p>
      <h2>2. Intellectual Property</h2>
      <p>
        All content, trademarks, and data on this site are the property of the company and may not
        be reproduced without permission.
      </p>
      <h2>3. Limitation of Liability</h2>
      <p>
        We are not responsible for any damages arising from the use of this site or the products
        purchased from it.
      </p>
    </div>
  );
};

export default TermsOfService;
